mapper.select:
SELECT a.article_id, a.user_id, a.title, a.content, a.text, a.browse_num, a.comment_num, a.praise_num, a.create_time, b.name, b.img from blog_article a LEFT JOIN blog_user b ON a.user_id=b.user_id where 1=1
and DATE_FORMAT(a.create_time,"%Y-%m")=#{pm.article_month}
and ( a.content like CONCAT(CONCAT('%', #{pm.keyword}), '%') or a.text like CONCAT(CONCAT('%', #{pm.keyword}), '%') or a.title like CONCAT(CONCAT('%', #{pm.keyword}), '%') )
and (
a.text like '%${item}%' or a.title like '%${item}%'
)
and a.user_id=#{pm.user_id}
and isdel=0 order by a.create_time desc,a.update_time desc

SELECT a.article_id, a.user_id, a.title, a.content, a.text, a.browse_num, a.comment_num, a.praise_num, a.create_time, b.name, b.img from blog_article a LEFT JOIN blog_user b ON a.user_id=b.user_id where a.article_id in
#{item.article_id}
and isdel=0 order by a.create_time desc,a.update_time desc

mapper.update:
update blog_article
	set isdel = 1
where article_id =#{article_id}
