mapper.select:
SELECT a.leaveword_id, a.content, a.address, a.browse_version, a.os_name, a.create_time, b.name, b.img from blog_leaveword a LEFT JOIN blog_user b ON a.user_id=b.user_id where 1=1
and leaveword_id=#{pm.leaveword_id}
order by a.create_time asc

SELECT a.comment_id, a.parent_id, a.table_id, a.user_id, a.reply_user_id, a.content, a.create_time, a.praise_num, a.browse_version, a.address, a.os_name, b.`name` as userName, b.img as userPath from blog_comment a LEFT JOIN blog_user b ON a.user_id = b.user_id WHERE table_id=#{pm.table_id}
and (a.parent_id is null or a.parent_id='')
and comment_id=#{pm.comment_id}
order by a.create_time asc,a.praise_num desc

SELECT a.comment_id, a.parent_id, a.table_id, a.user_id, a.reply_user_id, a.content, a.create_time, a.praise_num, a.browse_version, a.address, a.os_name, b.`name` as userName, b.img as userPath, c.name as replyUserName from blog_comment a LEFT JOIN blog_user b ON a.user_id = b.user_id left join blog_user c on a.reply_user_id=c.user_id WHERE table_id=#{table_id}
and (a.parent_id is not null or a.parent_id != '')
and comment_id=#{comment_id}
order by a.praise_num desc,a.create_time asc
